// custom.d.ts or any other .d.ts file in your project

// Declarations for .glsl
declare module '*.glsl' {
    const value: string;
    export default value;
}

// Declarations for .vert
declare module '*.vert' {
    const value: string;
    export default value;
}

// Declarations for .frag
declare module '*.frag' {
    const value: string;
    export default value;
}
